// Mapping for categorical climate values to numeric for charting
export const climateCategoryToNumeric = {
  "Flood Hazard Level": (v: ClimateRecord["Flood Hazard Level"]) => v === "Low" ? 1 : v === "Medium" ? 2 : 3,
  "Air Quality Index": (v: ClimateRecord["Air Quality Index"]) => v === "Good" ? 50 : v === "Moderate" ? 100 : 150
} as const;

