// Helper to get monthly series for a selected year and metric
export function getMonthlySeriesForClimate(year: Year, key: keyof ClimateRecord): number[] {
  const rows = climateData.filter(r => r.Year === year).sort((a,b)=>a.Month-b.Month);
  return rows.map(r => {
    const v = r[key];
    if (key === "Flood Hazard Level") {
      return climateCategoryToNumeric["Flood Hazard Level"](v as ClimateRecord["Flood Hazard Level"]);
    }
    if (key === "Air Quality Index") {
      return climateCategoryToNumeric["Air Quality Index"](v as ClimateRecord["Air Quality Index"]);
    }
    return v as number;
  });
}

export function getMonthlySeriesForSocietal(year: Year, key: keyof SocietalRecord): number[] {
  const rows = societalData.filter(r => r.Year === year).sort((a,b)=>a.Month-b.Month);
  return rows.map(r => r[key] as number);
}
